import "./App.css";
import { useState, useEffect } from "react";
import SearchBooks from "./SearchBooks";
import BookShelf from "./BookShelf";
import * as BooksAPI from "./BooksAPI";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);

  const [booksOnShelf, setBooksOnShelf] = useState([]);

  useEffect(() => {
    const getBooksOnShelf = async () => {
      const res = await BooksAPI.getAll();
      setBooksOnShelf(res);
    };
    getBooksOnShelf();
  }, []);

  const updateShelf = (book, shelf) => {
    const add = async () => {
      const res = await BooksAPI.update(book, shelf);
      setBooks(books.concat(res));
    };

    add();
    setShowSearchpage(!showSearchPage)
  };

  return (
    <div className="app">
      {showSearchPage ? (
        <SearchBooks books={booksOnShelf} handleOnClick={() => setShowSearchpage(!showSearchPage)} />
      ) : (
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
              <BookShelf 
                books={books} 
                shelf="currentlyReading" 
                title="Currently Reading" 
                onUpdateShelf={(book, shelf) => {updateShelf(book, shelf)}} 
              />
              <BookShelf 
                books={books} 
                shelf="wantToRead" 
                title="Future Reading" 
                onUpdateShelf={(book, shelf) => {updateShelf(book, shelf)}} 
              />
              <BookShelf 
                books={books} 
                shelf="read" 
                title="Finished Reading" 
                onUpdateShelf={(book, shelf) => {updateShelf(book, shelf)}} 
              />
            </div>
          </div>
          <div className="open-search">
            <button onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
