import "./App.css";
import { useState, useEffect } from "react";
import { Link, Route, Routes } from "react-router-dom";
import SearchBooks from "./SearchBooks";
import BookShelf from "./BookShelf";
import * as BooksAPI from "./BooksAPI";

function App() {

  const [allBooks, setAllBooks] = useState([]);

  useEffect(() => {
    const getAllBooks = async () => {
      const res = await BooksAPI.getAll();
      setAllBooks(res);
    };
    getAllBooks();
  }, []);

  let booksOnShelf = [];
  const getBookshelf = (shelf) => {
    booksOnShelf = allBooks.filter(book => book.shelf === shelf);
  }


  const updateShelf = (book, currentShelf, newShelf) => {
    const update = async () => {
      BooksAPI.update(book, newShelf); // update data on server
      allBooks.filter((book) => book.shelf = newShelf); // update global books
      getBookshelf(currentShelf);
      getBookshelf(newShelf);
    };
    update();
  }

  return (
    <Routes>
      <Route
        path="/search"
        element={
          <SearchBooks allBooks={allBooks} updateShelf={updateShelf} />
        } />
      <Route
        exact
        path="/"
        element={
          <div className="list-books">
            <div className="list-books-title">
              <h1>MyReads</h1>
            </div>
            <div className="list-books-content">
              <div>
              <BookShelf booksOnShelf={allBooks.filter(book => book.shelf === "currentlyReading")} updateShelf={updateShelf} key={"currentlyReading"} shelfTitle="Currently Reading"/>
