import "./App.css";
import { useState, useEffect } from "react";
import SearchBooks from "./SearchBooks";
import BookShelf from "./BookShelf";
import * as BooksAPI from "./BooksAPI";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);

  const [booksOnShelf, setBooksOnShelf] = useState([]);

  useEffect(() => {
    const getBooksOnShelf = async () => {
      const res = await BooksAPI.getAll();
      setBooksOnShelf(res);
    };
    getBooksOnShelf();

  }, []);



  return (
    <div className="app">
      {showSearchPage ? (
        <SearchBooks handleOnClick={() => setShowSearchpage(!showSearchPage)} updateShelf={updateShelf()} />
      ) : (
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
              <BookShelf 
                booksOnShelf={booksOnShelf} 
                shelf="currentlyReading" 
                title="Currently Reading" 
                updateShelf={updateShelf()} 
              />
              <BookShelf 
                booksOnShelf={booksOnShelf} 
                shelf="wantToRead" 
                title="Future Reading" 
                updateShelf={updateShelf()} 
              />
              <BookShelf 
                booksOnShelf={booksOnShelf} 
                shelf="read" 
                title="Finished Reading" 
                updateShelf={updateShelf()} 
              />
            </div>
          </div>
          <div className="open-search">
            <button onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
