import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import Book from "./Book";

const BookShelf = ({ allBooks, onUpdateShelves }) => {

    const [booksOnShelf, setBooksOnShelf] = useState([]);

    useEffect(() => { 
      if (Array.isArray(allBooks) && allBooks.length) {
        const getBooksOnShelf = () => {
          let newBookShelf = [];
          allBooks.forEach((book) => {
            if (book.shelf === shelfKey) { 
              newBookShelf.push(book);
            }
            setBooksOnShelf(newBookShelf);
          })
        }
        getBooksOnShelf();
      }
    }, [allBooks, shelfKey])

    const updateBookShelf = (book, newShelf) => {
      let updatedBooksOnShelf = [];
      if (newShelf === shelfKey) {
        updatedBooksOnShelf = booksOnShelf.push(book);
      }
      setBooksOnShelf(updatedBooksOnShelf);
    }

    return (
        <div className="bookshelf">
            <h2 className="bookshelf-title">{shelfTitle}</h2>
            <div className="bookshelf-books">
                <ol className="books-grid">
                    {booksOnShelf.forEach((book) =>
                        <Book
                            key={book.id}
                            book={book}
                            onUpdateShelves={onUpdateShelves}
                            onUpdateBookShelf={updateBookShelf}
                        />)
                    }
                </ol>
            </div>
        </div>
    )
}

BookShelf.propTypes = {
    allBooks: PropTypes.array.isRequired,
    shelfTitle: PropTypes.string.isRequired,
    shelfKey: PropTypes.string.isRequired,
    onUpdateShelves: PropTypes.func.isRequired,
};

export default BookShelf;
