import BookShelfChanger from "./BookShelfChanger";
import PropTypes from "prop-types";

const Book = ({ book }) => {
    let authorArray = [];
    const authors = book.authors;
    if (book.authors) {
        authorArray = authors.map((author) => author);
    }

    let imgURL = "";

    if (book.imageLinks) {
        imgURL = book.imageLinks
    }
    return (
        <li>
            <div className="book">
                <div className="book-top">
                    <div
                        className="book-cover"
                        style={{
                            width: 128,
                            height: 193,
                            backgroundImage: `url("${book.imageLinks.thumbnail ? book.imageLinks.thumbnail : ""}")`
                        }}
                    ></div>
                <BookShelfChanger book={book} />
                </div>
                <div className="book-title">{book.title ? book.title : "No Title"}</div>
                <div className="book-authors">
                    {authors ? authorArray.join(", ") : "No Author"}
                </div>
            </div>
        </li>
    )
}

Book.propTypes = {
    book: PropTypes.object.isRequired,
  };

export default Book;
