                    <ol className="books-grid">
                        {displayBooks.map((book) => (
                            <Book key={book.id} book={book} />
                        ))}
                    </ol>
                }
            </div>
        </div>
    )
}

SearchBooks.propTypes = {
    handleOnClick: PropTypes.func.isRequired,
};

export default SearchBooks;
