import "./App.css";
import { useState, useEffect } from "react";
import { Link, Route, Routes } from "react-router-dom";
import SearchBooks from "./SearchBooks";
import BookShelf from "./BookShelf";
import * as BooksAPI from "./BooksAPI";

function App() {

  const [allBooks, setAllBooks] = useState([]);

  useEffect(() => {
    const getAllBooks = async () => {
      const res = await BooksAPI.getAll();
      setAllBooks(res);
      console.log(res);
    };
    getAllBooks();
    console.log(allBooks)
  }, []);


  const updateShelves = (book, newShelf) => {
    const update = async () => {
      BooksAPI.update(book, newShelf); // update data on server
      allBooks.filter((book) => book.shelf = newShelf); // update global books
    };
    update();
  }

  return (
    <Routes>
      <Route
        path="/search"
        element={
          <SearchBooks allBooks={allBooks} updateShelves={updateShelves} />
        } />
      <Route
        exact
        path="/"
        element={
          <div className="list-books">
            <div className="list-books-title">
              <h1>MyReads</h1>
            </div>
            <div className="list-books-content">
              <div>
              <BookShelf allBooks={allBooks} updateShelves={updateShelves} shelfKey={"currentlyReading"} shelfTitle="Currently Reading"/>
              <BookShelf allBooks={allBooks} updateShelves={updateShelves} shelfKey={"wantToRead"} shelfTitle="Future Reading"/>
              <BookShelf allBooks={allBooks} updateShelves={updateShelves} shelfKey={"read"} shelfTitle="Past Reading"/>
              </div>
            </div>
            <div className="open-search">
              <Link to="/search" className="">Add a book</Link>
            </div>
          </div>
        }
      />
    </Routes>
  );
}

export default App;
