import Book from "./Book";

const BookShelf = ({ books, currentShelf }) => {
    return (
        <div className="bookshelf-books">
            <ol className="books-grid">
                {books
                    .filter(book => book.shelf === shelf)
                    .map(book => <Book book="book" shelf={shelf}/>)}
            </ol>
        </div>
    )
}

export default BookShelf;
