import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import Book from "./Book";

const BookShelf = ({ booksOnShelf, shelf, title, onUpdateCurrentShelf }) => {
    const [currentBooksOnShelf, setCurrentBooksOnShelf] = useState([]);

    useEffect(() => {
        const getCurrentBooksOnShelf = async () => {
          let filteredBooks = booksOnShelf.filter(book => book.shelf === shelf);
          setCurrentBooksOnShelf(filteredBooks);
        };
        getBooksOnShelf();
      }, []);



    return (
        <div className="bookshelf">
            <h2 className="bookshelf-title">{title}</h2>
            <div className="bookshelf-books">
                <div className="bookshelf-books">
                    <ol className="books-grid">
                        {currentBooksOnShelf
                            .map(book => <Book key={book.id} book={book} onUpdateCurrentShelf={onUpdateCurrentShelf} onMovingShelf={moveShelf} />)
                        }
                    </ol>
                </div>
            </div>
        </div>
    )
}

BookShelf.propTypes = {
    booksOnShelf: PropTypes.array.isRequired,
    shelf: PropTypes.string.isRequired,
    title: PropTypes.string.isRequired,
    onUpdateCurrentShelf: PropTypes.func.isRequired,
  };

export default BookShelf;
