import { useState } from "react";
import PropTypes from "prop-types";
import Book from "./Book";

const BookShelf = ({ allBooks, updateShelf, shelfName, shelfTitle }) => {

    const [booksOnShelf, setBooksOnShelf] = useState([]);
    const [currentShelf, setCurrentShelf] = useState([]);

    const updateBookshelf = (newShelf) => {
        let newBooks = allBooks.filter((book) => book.shelf === newShelf);
        setBooksOnShelf(newBooks);
    }

    return (
        <div className="bookshelf">
            <h2 className="bookshelf-title">{shelfTitle}</h2>
            <div className="bookshelf-books">
                <div className="bookshelf-books">
                    <ol className="books-grid">
                        {booksOnShelf.forEach((book) =>
                            <Book
                                key={book.id}
                                book={book}
                                currentShelf={currentShelf}
                                updateCurrentShelf={updateCurrentShelf}
                            />)
                        }
                    </ol>
                </div>
            </div>
        </div>
    )
}

BookShelf.propTypes = {
    allBooks: PropTypes.array.isRequired,
    shelfTitle: PropTypes.string.isRequired,
    updateShelf: PropTypes.func.isRequired,
};

export default BookShelf;
