import { useEffect } from "react";

import Book from "./Book";

const Booklist = ({ booksInList, onUpdateBookshelf }) => {

  useEffect(() => {
    console.log(booksInList);
  }, [booksInList]);

  const updateBooklist = (book, oldShelf, newShelf) => {
    onUpdateBookshelf(book, oldShelf, newShelf);
  }
  return (
    <ol className="books-grid">
      {
        booksInList.map((book) => {
          return (
            <Book
            key={book.id}
            book={book}
            onUpdateBooklist={updateBooklist}
          />
          )
        })
      }
    </ol>
  )
};

export default Booklist;
