import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import Book from "./Book";

const BookShelf = ({ allBook, title, onUpdateGlobalData }) => {
    const [booksOnShelf, setBooksOnShelf] = useState([booksOnShelf]);

    const updateShelf = () => {
        setBooksOnShelf(updatedBookshelf);
    }


    return (
        <div className="bookshelf">
            <h2 className="bookshelf-title">{title}</h2>
            <div className="bookshelf-books">
                <div className="bookshelf-books">
                    <ol className="books-grid">
                        {currentBooksOnShelf
                            .map(book => 
                                <Book 
                                    key={book.id} 
                                    book={book} 
                                    onUpdateGlobalData={onUpdateGlobalData} 
                                    onUpdateShelf={updateShelf}    
                                />)
                        }
                    </ol>
                </div>
            </div>
        </div>
    )
}

BookShelf.propTypes = {
    booksOnShelf: PropTypes.array.isRequired,
    title: PropTypes.string.isRequired,
    onUpdateGlobalData: PropTypes.func.isRequired,
  };

export default BookShelf;
