import { useEffect } from "react";
import PropTypes from "prop-types";
import Booklist from "./Booklist";

const Bookshelf = ({ allBooks, onUpdateAPI }) => {
  useEffect(() => {
    console.log(allBooks);
  }, [allBooks]);

  const listOfShelves = [
    {"title" : "Currently Reading", "type" : "currentlyReading"},
    {"title" : "Future Reading", "type" : "wantToRead"},
    {"title" : "Past Reading", "type" : "read"}
  ]
  let booksByShelf = [];

  listOfShelves.forEach((shelf) => {
    let currentShelf = allBooks.filter((book) => book.shelf = shelf.type);
    booksByShelf[shelf.type] = currentShelf;
  });

  const updateBookshelf = (book, oldShelf, newShelf) => {
    listOfShelves[oldShelf].pop(book);
    listOfShelves[newShelf].push(book);
    onUpdateAPI(book, newShelf);
  }

  return (
    <div>
      {listOfShelves.forEach((shelf) => {
        <div className="bookshelf">
          <h2 className="bookshelf-title">{shelf.title}</h2>
          <div className="bookshelf-books">
            <Booklist booksInList={booksByShelf[shelf]} onUpdateBookshelf={updateBookshelf} />
          </div>
        </div>
      })}
    </div>
  )
}

Bookshelf.propTypes = {
  allBooks: PropTypes.array.isRequired,
};

export default Bookshelf;
