import { useState } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import * as BooksAPI from "./BooksAPI";
import Book from "./Book";

const SearchBooks = ({ allBooks, updateShelf }) => {
    const [query, setQuery] = useState([]);

    const [booksInSearch, setBooksInSearch] = useState([]);

    const searchBooks = (query) => {
        setQuery(query);
        BooksAPI
            .search(query, 20)
            .then((response) => {
                if (!response.error) {
                    response.forEach((bookInSearch) => {
                        allBooks.forEach((book) => {
                            if (book.id === bookInSearch.id) {
                                bookInSearch.shelf = book.shelf;
                            }
                        })
                    });
                }
                setBooksInSearch(response);
            })
            .catch((e) => {
                setBooksInSearch([]);
            });
    }

    return (
        <div className="search-books">
            <div className="search-books-bar">
                <Link
                    to="/"
                    className="close-search"
                >
                    Close
                </Link>
                <div className="search-books-input-wrapper">
                    <input
                        type="text"
                        placeholder="Search by title, author, or ISBN"
                        value={query}
                        onChange={(event) => searchBooks(event.target.value)}
                    />
                </div>
            </div>
            <div className="search-books-results">
                {(Array.isArray(booksInSearch) && booksInSearch.length > 0) &&
                    <ol className="books-grid">
                        {booksInSearch.map((book) => (
                            <Book key={book.id} book={book} onUpdateGlobalData={updateShelf}/>
                        ))}
                    </ol>
                }
            </div>
        </div>
    )
}

SearchBooks.propTypes = {
    allBooks: PropTypes.array.isRequired,
    onUpdateGlobalData: PropTypes.func.isRequired
};

export default SearchBooks;
