import { useState, useEffect, useRef } from "react";

import PropTypes from "prop-types";

const BookShelfChanger = ({ book }) => {
    const [shelf, setShelf] = useState(bo);
    const selectedShelf = useRef(book.shelf);

    useEffect(() => {
        const getShelf = () => {
            if (book.hasOwnProperty("shelf")) {
                selectedShelf.current = book.shelf;
            } else {
                book.shelf = "none";
                selectedShelf.current = book.shelf;
            }

            setShelf(selectedShelf);
        };

        getShelf();

    }, [book]);

    const onUpdateShelf = (event) => {
        setShelf(event.target.value);
        console.log(shelf);
    };

    return (
        <div className="book-shelf-changer">
            <select value={shelf} onChange={(event) => onUpdateShelf(event)}>
                <option value="none" disabled>Move to...</option>
                <option value="currentlyReading">Currently Reading</option>
                <option value="wantToRead">Want to Read</option>
                <option value="read">Read</option>
                <option value="none">None</option>
            </select>
        </div>
    )
}

BookShelfChanger.propTypes = {
    book: PropTypes.object.isRequired,
};

export default BookShelfChanger;
