import "./App.css";
import { useState, useEffect } from "react";
import { Link, Route, Routes } from "react-router-dom";
import SearchBooks from "./SearchBooks";
import BookShelf from "./BookShelf";
import * as BooksAPI from "./BooksAPI";

function App() {

  const [allBooks, setAllBooks] = useState([]);

  useEffect(() => {
    const getAllBooks = async () => {
      const res = await BooksAPI.getAll();
      setAllBooks(res);
    };
    getAllBooks();
  }, []);

  const updateRemoteData = (book, shelf) => {
    const update = async () => {
      BooksAPI.update(book, shelf); // update data on server
    };
    update();
  }

  return (
    <Routes>
      <Route
        path="/search"
        element={
          <SearchBooks allBooks={allBooks} onUpdateRemoteData={updateRemoteData}/>
        } />
      <Route
        exact
        path="/"
        element={
          <div className="list-books">
            <div className="list-books-title">
              <h1>MyReads</h1>
            </div>
            <div className="list-books-content">
              <div>
                <BookShelf
                  currentBooks={allBooks.filter(book => book.shelf === "currentlyReading")}
                  onUpdateRemoteData={updateRemoteData}
                  title="Currently Reading"
                />
                <BookShelf
                  currentBooks={allBooks.filter(book => book.shelf === "currentlyReading")}
                  onUpdateRemoteData={updateRemoteData}
                  shelf="wantToRead"
                  title="Future Reading"
                />
                <BookShelf
                  allBooks={allBooks}
                  onUpdateRemoteData={updateRemoteData}
                  shelf="read"
                  title="Finished Reading"
                />
              </div>
            </div>
            <div className="open-search">
              <Link to="/search" className="">Add a book</Link>
            </div>
          </div>
        }
      />
    </Routes>
  );
}

export default App;
