import "./App.css";
import { useState, useEffect } from "react";
import { Link, Route, Routes } from "react-router-dom";
import SearchBooks from "./SearchBooks";
import BookShelf from "./BookShelf";
import * as BooksAPI from "./BooksAPI";

function App() {

  const [booksOnShelf, setBooksOnShelf] = useState([]);

  useEffect(() => {
    const getBooksOnShelf = async () => {
      const res = await BooksAPI.getAll();
      setBooksOnShelf(res);
    };
    getBooksOnShelf();
  }, []);

  const updateCurrentShelf = (book, value) => {
    const update = async () => {
      BooksAPI.update(book, value); // update on server
      // update local s
    };

    update();
  }

  return (
    <Routes>
      <Route
        path="/search"
        element={
          <SearchBooks booksOnShelf={booksOnShelf} onUpdateCurrentShelf={updateCurrentShelf()}/>
        } />
      <Route
        exact
        path="/"
        element={
          <div className="list-books">
            <div className="list-books-title">
              <h1>MyReads</h1>
            </div>
            <div className="list-books-content">
              <div>
                <BookShelf
                  booksOnShelf={booksOnShelf}
                  shelf="currentlyReading"
                  title="Currently Reading"
                />
                <BookShelf
                  booksOnShelf={booksOnShelf}
                  shelf="wantToRead"
                  title="Future Reading"
                />
                <BookShelf
                  booksOnShelf={booksOnShelf}
                  shelf="read"
                  title="Finished Reading"
                />
              </div>
            </div>
            <div className="open-search">
              <Link to="/search" className="">Add a book</Link>
            </div>
          </div>
        }
      />
    </Routes>
  );
}

export default App;
