import "./App.css";
import { useState, useEffect } from "react";
import showSearchPage from "./components/Se"
import Reading from "./components/Reading";
import FutureReads from "./components/FutureReads";
import PastReads from "./components/PastReads";
import * as BooksAPI from "./BooksAPI";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);

  const [books, setBooks] = useState([]);

  useEffect(() => {
    const getBooks = async () => {
      const res = await BooksAPI.getAll();
      setBooks(res);
    };
    getBooks();
  }, []);

  return (
    <div className="app">
      {showSearchPage ? (
<SearchBooks showSearchPage={showSearchPage} setShowSearchpage={setShowSearchpage}/>
      ) : (
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
              <Reading />
              <FutureReads />
              <PastReads />
            </div>
          </div>
          <div className="open-search">
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
