import "./App.css";
import { useState, useEffect } from "react";
import { Link, Route, Routes } from "react-router-dom";
import SearchBooks from "./SearchBooks";
import BookShelf from "./BookShelf";
import * as BooksAPI from "./BooksAPI";

const App = () => {

  const [allBooks, setAllBooks] = useState([]);

  useEffect(() => {

    if (allBooks.length === 0) {
      const getBooks = async () => {
        const res = await BooksAPI.getAll();
        setAllBooks(res);

      };
      getBooks();
      console.log(allBooks);
    }

  }, [allBooks]);


  const updateShelves = (book, newShelf) => {
    const update = async () => {
      BooksAPI.update(book, newShelf); // update data on server
      allBooks.filter((book) => book.shelf = newShelf); // update global books
    };
    update();
  }

  return (
    <Routes>
      <Route
        path="/search"
        element={
          <SearchBooks allBooks={allBooks} onUpdateShelves={updateShelves} />
        } />
      <Route
        exact
        path="/"
        element={
          <div className="list-books">
            <div className="list-books-title">
              <h1>MyReads</h1>
            </div>
            <div className="list-books-content">
              <div>
                <BookShelf allBooks={allBooks} onUpdateShelves={updateShelves} shelfKey={"currentlyReading"} shelfTitle="Currently Reading" />
                <BookShelf allBooks={allBooks} onUpdateShelves={updateShelves} shelfKey={"wantToRead"} shelfTitle="Future Reading" />
                <BookShelf allBooks={allBooks} onUpdateShelves={updateShelves} shelfKey={"read"} shelfTitle="Past Reading" />
              </div>
            </div>
            <div className="open-search">
              <Link to="/search" className="">Add a book</Link>
            </div>
          </div>
        }
      />
    </Routes>
  );
}

export default App;
