import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import Book from "./Book";

const Bookshelf = ({ allBooks, onUpdateShelves }) => {

  const listOfShelves = [
    {"title" : "Currently Reading", "type" : "currentlyReading"},
    {"title" : "Future Reading", "type" : "wantToRead"},
    {"title" : "Past Reading", "type" : "read"}
  ]
  let booksByShelf = {};

  listOfShelves.forEach((shelf) => {
    allBooks.filter((book) => {
      if (booksByShelf[shelf]) {
        booksByShelf[shelf].push(book);
      } else {
        booksByShelf[shelf] = book;
      }
    })
  });

  return (
    <div>
      {listOfShelves.map((shelf) => {
        booksByShelf.map((book) => book.shelf = shelf.type);
        <div className="bookshelf">
          <h2 className="bookshelf-title">{shelf.title}</h2>
          <div className="bookshelf-books">

          </div>
        </div>
      })}
    </div>
  )
}

Bookshelf.propTypes = {
  allBooks: PropTypes.array.isRequired,
  shelfTitle: PropTypes.string.isRequired,
  shelfKey: PropTypes.string.isRequired,
  onUpdateShelves: PropTypes.func.isRequired,
};

export default Bookshelf;
