import { useState } from "react";
import PropTypes from "prop-types";
import Book from "./Book";

const BookShelf = ({ allBooks, updateShelves, shelfKey, shelfTitle }) => {

    const [booksOnShelf, setBooksOnShelf] = useState([allBooks.filter(book)]);
    const [currentShelf, setNewShelf] = useState(shelfKey);

    const updateCurrentShelf = (newShelf) => {
        let newBooks = allBooks.filter((book) => book.shelf === newShelf);
        setBooksOnShelf(newBooks);
        setNewShelf(newShelf);
    }

    return (
        <div className="bookshelf">
            <h2 className="bookshelf-title">{shelfTitle}</h2>
            <div className="bookshelf-books">
                <ol className="books-grid">
                    {booksOnShelf.forEach((book) =>
                        <Book
                            key={book.id}
                            book={book}
                            currentShelf={currentShelf}
                            updateCurrentShelf={updateCurrentShelf}
                            updateShelves={updateShelves}
                        />)
                    }
                </ol>
            </div>
        </div>
    )
}

BookShelf.propTypes = {
    allBooks: PropTypes.array.isRequired,
    shelfTitle: PropTypes.string.isRequired,
    currentShelf: PropTypes.string.isRequired,
    updateShelves: PropTypes.func.isRequired,
};

export default BookShelf;
