import "./App.css";
import { useState, useEffect } from "react";
import { Link, Route, Routes } from "react-router-dom";
import SearchBooks from "./SearchBooks";
import BookShelf from "./BookShelf";
import * as BooksAPI from "./BooksAPI";

function App() {

  const [allBooks, setAllBooks] = useState([]);

  useEffect(() => {
    const getAllBooks = async () => {
      const res = await BooksAPI.getAll();
      setAllBooks(res);
    };
    getAllBooks();
  }, []);

  const shelves = {
    currentlyReading: "Currently Reading",
    wantToRead: "Future Reading",
    read: "Past Reading"
  }

  const updateShelf = (book, shelf) => {
    const update = async () => {
      BooksAPI.update(book, shelf); // update data on server
      allBooks.filter((book) => book.shelf = shelf); // update global books
    };
    update();
  }

  return (
    <Routes>
      <Route
        path="/search"
        element={
          <SearchBooks allBooks={allBooks} updateShelf={updateShelf} />
        } />
      <Route
        exact
        path="/"
        element={
          <div className="list-books">
            <div className="list-books-title">
              <h1>MyReads</h1>
            </div>
            <div className="list-books-content">
              <div>
                {shelves.forEach((shelf) => 
                <BookShelf booksOnShelf={allBooks.filter((book) => book.shelf === shelf)} updateShelf={updateShelf} shelfName={shelves[shelf]} shelfTitle={shelf}/>
                )}
              </div>
            </div>
            <div className="open-search">
              <Link to="/search" className="">Add a book</Link>
            </div>
          </div>
        }
      />
    </Routes>
  );
}

export default App;
