import { useState, useEffect } from "react";
import PropTypes from "prop-types";
import Booklist from "./Booklist";

const Bookshelf = ({ allBooks, onUpdateAPI }) => {
  const [currentBooks, setCurrentBooks] = useState([]);
  const [futureBooks, setFutureBooks] = useState([]);
  const [pastBooks, setPastBooks] = useState([]);

  const listOfShelves = [
    {"title" : "Currently Reading", "type" : "currentlyReading"},
    {"title" : "Future Reading", "type" : "wantToRead"},
    {"title" : "Past Reading", "type" : "read"}
  ]

  useEffect(() => {
      const getAllShelves = () => {
        const currentlyReading = allBooks.filter((book) => book.shelf === "currentlyReaindg");
        setCurrentBooks(currentlyReading);
        const wantToRead = allBooks.filter((book) => book.shelf === "wantToRead");
        setFutureBooks(wantToRead);
        const read = allBooks.filter((book) => book.shelf === "read");
        setPastBooks(read);
      }
      getAllShelves();

  }, [allBooks]);

  const updateBookshelf = (book, oldShelf, newShelf) => {
    if (oldShelf === "currentlyReading") {
      setCurrentBooks(currentBooks.pop(book));
    }
    if (oldShelf === "wantToRead") {
      setFutureBooks(futureBooks.pop(book));
    }
    if (oldShelf === "read") {
      setPastBooks(pastBooks.pop(book));
    }

    if (newShelf === "currentlyReading") {
      setCurrentBooks(currentBooks.push(book));
    }
    if (newShelf === "wantToRead") {
      setFutureBooks(futureBooks.push(book));
    }
    if (newShelf === "read") {
      setPastBooks(pastBooks.push(book));
    }

    onUpdateAPI(book, newShelf);
  }

  return (
    <div>
      {listOfShelves.forEach((shelf) => {
        let shelfType = shelf.type;
        let booksInList = [];

        if (shelf.type = "currentlyReading") {
          booksInList = currentBooks;
        }

        if (shelf.type = "wantToRead") {
          booksInList = futureBooks;
        }

        if (shelf.type = "read") {
          booksInList = pastBooks;
        }

        <div className="bookshelf">
          <h2 className="bookshelf-title">{shelf.title}</h2>
          <div className="bookshelf-books">
            <Booklist booksInList={booksInList} onUpdateBookshelf={updateBookshelf} />
          </div>
        </div>
      })}
    </div>
  )
}

Bookshelf.propTypes = {
  allBooks: PropTypes.array.isRequired,
};

export default Bookshelf;
