import "./App.css";
import { useState, useEffect } from "react";
import SearchBooks from "./components/SearchBooks";
import BookShelf from "./components/BookShelf";
import Reading from "./components/Reading";
import FutureReads from "./components/FutureReads";
import PastReads from "./components/PastReads";
import * as BooksAPI from "./BooksAPI";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);

  const [books, setBooks] = useState([]);

  useEffect(() => {
    const getBooks = async () => {
      const res = await BooksAPI.getAll();
      setBooks(res);
    };
    getBooks();
  }, []);

  const addBook = (book, shelf) => {
    const add = async () => {
      const res = await BooksAPI.update(book, shelf);
      setBooks(books.concat(res));
    };

    add();
    setShowSearchpage(!showSearchPage)
  };

  return (
    <div className="app">
      {showSearchPage ? (
        <SearchBooks books={books} handleOnClick={() => setShowSearchpage(!showSearchPage)} />
      ) : (
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
              <BookShelf books={books} shelf="currentlyRead" bookShelfTitle="Currently Reading" />
              <Reading books={books} />
              <FutureReads books={books} />
              <PastReads books={books} />
            </div>
          </div>
          <div className="open-search">
            <a onClick={() => setShowSearchpage(!showSearchPage)} onAddBook={(book, shelf) => {
              addBook(book, shelf);
            }}>Add a book</a>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
