import { useState, useEf } from "react";
import PropTypes from "prop-types";
import Book from "./Book";

const BookShelf = ({ allBooks, updateShelves, shelfKey, shelfTitle }) => {

    const [booksOnShelf, setBooksOnShelf] = useState([allBooks.filter((book) => book.shelf = shelfKey)]);

    useEffect(() => { 
      console.log(allBooks.filter((book) => book.shelf = shelfKey))
      }, [allBooks])

    const updateBookShelf = (book, newShelf) => {
      let updatedBooksOnShelf = [];
      if (newShelf === shelfKey) {
        updatedBooksOnShelf = booksOnShelf.push(book);
      }
      setBooksOnShelf(updatedBooksOnShelf);
    }

    return (
        <div className="bookshelf">
            <h2 className="bookshelf-title">{shelfTitle}</h2>
            <div className="bookshelf-books">
                <ol className="books-grid">
                    {booksOnShelf.forEach((book) =>
                        <Book
                            key={book.id}
                            book={book}
                            updateShelves={updateShelves}
                            onUpdateBookShelf={updateBookShelf}
                        />)
                    }
                </ol>
            </div>
        </div>
    )
}

BookShelf.propTypes = {
    allBooks: PropTypes.array.isRequired,
    shelfTitle: PropTypes.string.isRequired,
    shelfKey: PropTypes.string.isRequired,
    updateShelves: PropTypes.func.isRequired,
};

export default BookShelf;
